import React, { useRef, useEffect, useState } from "react";

const EYE_LEFT = [263, 249, 390, 373, 374, 380]; // MediaPipe Í≥µÏãù ÏôºÏ™Ω Îàà Ïù∏Îç±Ïä§
const EYE_RIGHT = [33, 7, 163, 144, 145, 153]; // MediaPipe Í≥µÏãù Ïò§Î•∏Ï™Ω Îàà Ïù∏Îç±Ïä§

const ViewCamera = () => {
  const videoRef = useRef(null);
  const [blinkCount, setBlinkCount] = useState(0);
  const [ear, setEar] = useState(0);
  const faceMeshRef = useRef(null);

  // Îàà ÍπúÎπ°ÏûÑ ÎπÑÏú®(EAR) Í≥ÑÏÇ∞ Ìï®Ïàò
  const calculateEAR = (landmarks, eyeIndices) => {
    const [p1, p2, p3, p4, p5, p6] = eyeIndices.map((i) => landmarks[i]);
    const vertical1 = Math.hypot(p2.x - p6.x, p2.y - p6.y);
    const vertical2 = Math.hypot(p3.x - p5.x, p3.y - p5.y);
    const horizontal = Math.hypot(p1.x - p4.x, p1.y - p4.y);
    return (vertical1 + vertical2) / (2 * horizontal);
  };

  // MediaPipe Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const script = document.createElement("script");
    script.src =
      "https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/face_mesh.js";
    script.onload = initializeFaceMesh;
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
      if (faceMeshRef.current) faceMeshRef.current.close();
    };
  }, []);

  const initializeFaceMesh = async () => {
    window.Module = {
      locateFile: (path) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${path}`,
    };

    faceMeshRef.current = new window.FaceMesh({
      locateFile: (file) =>
        `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`,
    });

    faceMeshRef.current.setOptions({
      maxNumFaces: 1,
      refineLandmarks: true,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    faceMeshRef.current.onResults(onResults);
    setupWebcam();
  };

  // ÏõπÏ∫† ÏÑ§Ï†ï
  const setupWebcam = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 640, height: 480 },
      });
      videoRef.current.srcObject = stream;
      videoRef.current.onloadedmetadata = () => videoRef.current.play();
      startDetection();
    } catch (error) {
      console.error("ÏõπÏ∫† Ï†ëÍ∑º Ïò§Î•ò:", error);
    }
  };

  // ÌîÑÎ†àÏûÑ Ï≤òÎ¶¨ ÏãúÏûë
  const startDetection = () => {
    const processFrame = async () => {
      if (videoRef.current?.readyState === 4) {
        await faceMeshRef.current.send({ image: videoRef.current });
      }
      requestAnimationFrame(processFrame);
    };
    processFrame();
  };

  // Í≤∞Í≥º Ï≤òÎ¶¨
  const onResults = (results) => {
    if (results.multiFaceLandmarks?.[0]) {
      const landmarks = results.multiFaceLandmarks[0];
      const leftEAR = calculateEAR(landmarks, EYE_LEFT);
      const rightEAR = calculateEAR(landmarks, EYE_RIGHT);
      const avgEAR = (leftEAR + rightEAR) / 2;
      setEar(avgEAR);

      if (avgEAR < 0.25) {
        // ÏûÑÍ≥ÑÍ∞í Ï°∞Ï†à ÌïÑÏöî Ïãú ÏàòÏ†ï
        setBlinkCount((prev) => prev + 1);
      }
    }
  };

  return (
    <div
      style={{
        position: "relative",
        width: 640,
        margin: "0 auto",
        backgroundColor: "#1a1a1a",
        borderRadius: 8,
        overflow: "hidden",
      }}
    >
      <video
        ref={videoRef}
        autoPlay
        playsInline
        style={{
          width: "100%",
          height: "auto",
          transform: "scaleX(-1)",
        }}
      />
      <div
        style={{
          position: "absolute",
          top: 20,
          left: 20,
          color: "white",
          backgroundColor: "rgba(0,0,0,0.7)",
          padding: "10px 20px",
          borderRadius: 8,
        }}
      >
        üëÅÔ∏è Îàà ÍπúÎπ°ÏûÑ ÌöüÏàò: <strong>{blinkCount}</strong>
      </div>
    </div>
  );
};

export default ViewCamera;
