import React, { useRef } from "react";
import Webcam from "react-webcam";
import axios from "axios";

function App() {
  const webcamRef = useRef(null);

  const capture = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    const response = await axios.post("http://localhost:3001/check-eye", {
      image: imageSrc,
    });
    console.log(response.data); // { status: "open" or "closed" }
  };

  return (
    <div>
      <Webcam ref={webcamRef} />
      <button onClick={capture}>눈 상태 확인</button>
    </div>
  );
}
export default App;
