import React, { useEffect, useRef, useState } from "react";

// MediaPipe Í≥µÏãù Î¨∏ÏÑú Í∏∞Ï§Ä Ï†ïÌôïÌïú Îàà ÎûúÎìúÎßàÌÅ¨ Ïù∏Îç±Ïä§
const EYE_LEFT = [263, 249, 390, 373, 374, 380]; // ÏôºÏ™Ω Îàà (ÏÇ¨Ïö©Ïûê ÏãúÏ†ê Í∏∞Ï§Ä)
const EYE_RIGHT = [33, 7, 163, 144, 145, 153]; // Ïò§Î•∏Ï™Ω Îàà

function calculateEAR(landmarks, eyeIndices) {
  const [p1, p2, p3, p4, p5, p6] = eyeIndices.map((i) => landmarks[i]);
  const vertical1 = Math.hypot(p2.x - p6.x, p2.y - p6.y);
  const vertical2 = Math.hypot(p3.x - p5.x, p3.y - p5.y);
  const horizontal = Math.hypot(p1.x - p4.x, p1.y - p4.y);
  return (vertical1 + vertical2) / (2 * horizontal);
}

// ÎèôÏ†Å ÏûÑÍ≥ÑÍ∞í ÏÑ§Ï†ïÏùÑ ÏúÑÌïú Ï¥àÍ∏∞Ìôî Î≥ÄÏàò
let EAR_THRESHOLD = 0.25;
let CONSEC_FRAMES = 3; // Îçî Ï†ïÌôïÌïú ÌåêÏ†ïÏùÑ ÏúÑÌï¥ ÌîÑÎ†àÏûÑ Ïàò Ï¶ùÍ∞Ä

const ViewCamera = () => {
  const videoRef = useRef(null);
  const [blinkCount, setBlinkCount] = useState(0);
  const [ear, setEar] = useState(0);
  const [frameCounter, setFrameCounter] = useState(0);
  const [isCalibrating, setIsCalibrating] = useState(true);

  // MediaPipe Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    let faceMesh = null;
    let animationId = null;
    const script = document.createElement("script");

    const initializeFaceMesh = async () => {
      try {
        faceMesh = new window.FaceMesh({
          locateFile: (file) => {
            // WASM ÌååÏùº Î°úÎìú Î¨∏Ï†ú Ìï¥Í≤∞
            if (file.includes("_wasm")) {
              return `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`;
            }
            return `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`;
          },
        });

        faceMesh.setOptions({
          maxNumFaces: 1,
          refineLandmarks: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5,
          selfieMode: true, // ÏÖÄÌîº Î™®Îìú ÌôúÏÑ±Ìôî
        });

        faceMesh.onResults(onResults);
        processFrame();
      } catch (error) {
        console.error("MediaPipe Ï¥àÍ∏∞Ìôî Ïã§Ìå®:", error);
      }
    };

    // MediaPipe Ïä§ÌÅ¨Î¶ΩÌä∏ ÎèôÏ†Å Î°úÎìú
    script.src =
      "https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/face_mesh.js";
    script.async = true;
    script.onload = initializeFaceMesh;
    document.body.appendChild(script);

    // ÌîÑÎ†àÏûÑ Ï≤òÎ¶¨ Î£®ÌîÑ
    const processFrame = async () => {
      if (videoRef.current?.readyState === 4) {
        try {
          await faceMesh.send({ image: videoRef.current });
        } catch (error) {
          console.error("ÌîÑÎ†àÏûÑ Ï≤òÎ¶¨ Ïò§Î•ò:", error);
        }
      }
      animationId = requestAnimationFrame(processFrame);
    };

    // Ï†ïÎ¶¨ Ìï®Ïàò
    return () => {
      if (animationId) cancelAnimationFrame(animationId);
      if (faceMesh) faceMesh.close();
      document.body.removeChild(script);
    };
  }, []);

  // ÏõπÏ∫† Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const setupWebcam = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: {
            width: 640,
            height: 480,
            facingMode: "user",
          },
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("ÏõπÏ∫† Ï†ëÍ∑º Ïò§Î•ò:", error);
      }
    };
    setupWebcam();
  }, []);

  // ÏûÑÍ≥ÑÍ∞í ÏûêÎèô Î≥¥Ï†ï
  useEffect(() => {
    if (isCalibrating) {
      const samples = [];
      const interval = setInterval(() => {
        if (samples.length < 30) {
          samples.push(ear);
        } else {
          const avg = samples.reduce((a, b) => a + b, 0) / samples.length;
          EAR_THRESHOLD = avg * 0.7; // Í∞úÏù∏Î≥Ñ ÏµúÏ†ÅÌôî
          setIsCalibrating(false);
          clearInterval(interval);
        }
      }, 100);

      return () => clearInterval(interval);
    }
  }, [ear, isCalibrating]);

  // Í≤∞Í≥º Ï≤òÎ¶¨
  const onResults = (results) => {
    if (results.multiFaceLandmarks?.[0]) {
      const landmarks = results.multiFaceLandmarks[0];
      const leftEAR = calculateEAR(landmarks, EYE_LEFT);
      const rightEAR = calculateEAR(landmarks, EYE_RIGHT);
      const avgEAR = (leftEAR + rightEAR) / 2;

      setEar(avgEAR);

      // ÍπúÎπ°ÏûÑ ÌåêÏ†ï Î°úÏßÅ
      if (avgEAR < EAR_THRESHOLD) {
        setFrameCounter((prev) => prev + 1);
      } else {
        if (frameCounter >= CONSEC_FRAMES) {
          setBlinkCount((prev) => prev + 1);
        }
        setFrameCounter(0);
      }
    }
  };

  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        maxWidth: 640,
        margin: "0 auto",
        backgroundColor: "#1a1a1a",
        borderRadius: "10px",
        overflow: "hidden",
      }}
    >
      <video
        ref={videoRef}
        autoPlay
        playsInline
        style={{
          width: "100%",
          height: "auto",
          transform: "scaleX(-1)", // ÎØ∏Îü¨ Î™®Îìú
        }}
      />

      <div
        style={{
          position: "absolute",
          top: 20,
          left: 20,
          color: "#fff",
          backgroundColor: "rgba(0,0,0,0.7)",
          padding: "10px 20px",
          borderRadius: 8,
          boxShadow: "0 2px 10px rgba(0,0,0,0.3)",
        }}
      >
        <div style={{ fontSize: 18, marginBottom: 5 }}>
          üëÅÔ∏è Blink Count: <strong>{blinkCount}</strong>
        </div>
        <div style={{ fontSize: 14 }}>
          EAR: {ear.toFixed(3)} (Threshold: {EAR_THRESHOLD.toFixed(3)})
        </div>
        {isCalibrating && (
          <div
            style={{
              color: "#ffd700",
              fontSize: 12,
              marginTop: 5,
            }}
          >
            Calibrating...
          </div>
        )}
      </div>

      {ear < EAR_THRESHOLD && (
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            fontSize: "2em",
            fontWeight: "bold",
            color: "#ff4444",
            textShadow: "0 2px 4px rgba(0,0,0,0.5)",
            animation: "blink 1s infinite",
          }}
        >
          BLINKING!
        </div>
      )}

      <style>{`
        @keyframes blink {
          0% { opacity: 1; }
          50% { opacity: 0.3; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default ViewCamera;
